import React, { useEffect, useRef } from 'react';
import * as d3 from 'd3';

const Tree = ({ data }) => {
  const chartRef = useRef(null);

  useEffect(() => {
    const svg = d3.select(chartRef.current);

    // Define the tree layout
    const treeLayout = d3.tree().size([width, height]);

    // Transform the hierarchical data into a tree structure
    const root = d3.hierarchy(data);
    const treeData = treeLayout(root);

    // Create links
    const links = treeData.links();
    svg
      .selectAll('.link')
      .data(links)
      .enter()
      .append('path')
      .attr('class', 'link')
      .attr('d', d3.linkHorizontal().x(d => d.y).y(d => d.x));

    // Create nodes
    const nodes = treeData.descendants();
    const node = svg
      .selectAll('.node')
      .data(nodes)
      .enter()
      .append('g')
      .attr('class', 'node')
      .attr('transform', d => `translate(${d.y},${d.x})`);

    node.append('circle').attr('r', 5); // Add circles for nodes

    node
      .append('text')
      .attr('dy', '0.35em')
      .attr('x', d => (d.children ? -10 : 10))
      .attr('text-anchor', d => (d.children ? 'end' : 'start'))
      .text(d => d.data.name); // Display node names

    // Clean up the SVG element when the component is unmounted
    return () => {
      svg.selectAll('*').remove();
    };
  }, [data]);

  return (
    <svg ref={chartRef} width={800} height={600}>
      {/* SVG content will be rendered here */}
    </svg>
  );
};

export default TreeChart;
